const defualtState = {
    btnSave: [],
  };


  const btnSave = (state = defualtState, { type, payload }) => {
    switch (type) {
      case "Show":
        return { List: payload};

      case "add":
        return { btnSave: [payload,...state.btnSave ] };

      default:
        return state;
    }
  };
  export default btnSave;

  export const changeState = (payload) => {
    return {
      type: "add",
      payload: payload,
    };
  };
  export const show = (payload) => {
    return {
      type: "Show",
      payload: payload,
    };
  };
  export const edit = (payload) => {
    return {
      type: "edit",
      payload: payload,
    };
  };
