import React from 'react';

import  { Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';


// Create styles
const styles = StyleSheet.create({
  page: {
    flexDirection: 'row',
    backgroundColor: '#E4E4E4'
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1
  }
});

function MyDocument(props) {
    return (
        <div
      style={{
        backgroundColor: "white",
        margin: 100,
        padding: 80,
        borderRadius: 19,
      }}
    >
      <div style={{ display: "flex", flexDirection: "row", gap: 700 }}>
        <h3>الفاتوره</h3>
        <img
          src="https://www.logodesign.net/logo/abstract-cuboid-building-4519ld.png"
          width={70}
          height={70}
        />
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "flex-end",
        }}
      >
        <h3>اسم الشركه</h3>
        <h3>رقم الجوال </h3>
        <h3>العنوان</h3>
      </div>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
        }}
      >
        <div>
          <h2>معلومات العميل </h2>
          <input
            type="text"
            placeholder="اسم العميل"
            style={{ display: "block", marginBottom: 10 }}
          />
          <input type="text" placeholder="رقم الجوال" />
        </div>

        <div>
          <h3>رقم الفاتورة</h3>
          <input type="text" placeholder="ادخل رقم الفاتورة" />
        </div>

        <div>
          <h3>تاريخ الفاتورة</h3>
          <input type="date" placeholder="ادخل تاريخ الفاتورة" />
        </div>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "row-reverse",
          justifyContent: "space-around",
          backgroundColor: "grey",
          marginTop: 20,
          marginBottom: 10,
        }}
      >
        <h3>اسم المنتج</h3>
        <h3>الكمية</h3>
        <h3>السعر</h3>
        <h3>المجموع</h3>
      </div>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
          marginLeft: 80,
        }}
      >
        <span>200</span>
        <input
          type="number"
          placeholder="سعر المنتج"
          style={{ width: 50, marginLeft: 50 }}
        />
        <input
          type="number"
          placeholder="الكمية"
          style={{ width: 50, marginLeft: 80 }}
        />
        <input type="text" placeholder="اسم المنتج" />
      </div>
      <hr />

      <div
        style={{
          display: "flex",
          flexDirection: "column",
          marginLeft: 80,
          paddingTop: 50,
        }}
      >
        <div>
          <h3 style={{ display: "inline", padding: 60 }}>المجموع</h3>
          <span style={{ display: "inline" }}>3500</span>
        </div>

        <div>
          <h3 style={{ display: "inline", padding: 60 }}>الضريبة المضافة</h3>
          <span style={{ display: "inline" }}>500</span>
        </div>

        <div>
          <h3 style={{ display: "inline", padding: 60 }}>الإجمالي</h3>
          <span style={{ display: "inline" }}>4000</span>
        </div>
      </div>
    </div>

    );
}

export default MyDocument;
