import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

const image = require("../../images/.../../images/login.jpeg")

function Login() {

  const handle = (e) => {
    localStorage.setItem("token", e);
    console.log(localStorage.getItem("token"));
  };

  async function getInfo(e) {
    e.preventDefault();

    console.log();
    let obj = {
      username: e.target.form[0].value,
      password: e.target.form[1].value,
    };
    console.log(obj)
    try {
      const res = await axios.post("http://localhost:3003/login", obj);
      console.log(res.data);
      handle(res.data);
    } catch (err) {
      console.log(err);
    }
  }

  return (
    <section className="vh-100" style={{backgroundColor: "grey"}}>
    <div className="container py-5 h-100">
      <div className="row d-flex justify-content-center align-items-center h-100">
        <div className="col col-xl-10">
          <div className="card" style={{borderRadius: "1rem"}}>
            <div className="row g-0">
              <div className="col-md-6 col-lg-5 d-none d-md-block">
                <img

                  src= {image}
                  alt="login form"
                  className="img-fluid" style={{borderRadius: "1rem 0 0 1rem"}}
                />
              </div>
              <div className="col-md-6 col-lg-7 d-flex align-items-center">
                <div className="card-body p-4 p-lg-5 text-black">
  
                  <form>
  
                    <div className="d-flex align-items-center mb-3 pb-1">
                      <i className="fas fa-cubes fa-2x me-3" style={{color: "#ff6219"}}></i>
                      <span className="h1 fw-bold mb-0">Logo</span>
                    </div>
  
                    <h5 className="fw-normal mb-3 pb-3" style={{letterSpacing: "1px"}}>Sign into your account</h5>
  
                    <div className="form-outline mb-4">
                      <input type="email" id="form2Example17" className="form-control form-control-lg" />
                      <label className="form-label" for="form2Example17">Email address</label>
                    </div>
  
                    <div className="form-outline mb-4">
                      <input type="password" id="form2Example27" className="form-control form-control-lg" />
                      <label className="form-label" for="form2Example27">Password</label>
                    </div>
  
                    <div className="pt-1 mb-4">
                      <button className="btn btn-dark btn-lg btn-block" type="button">Login</button>
                    </div>
  
                  </form>
  
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
  );
}

export default Login;
