import { usePDF } from "@react-pdf/renderer";
import React, { useEffect, useState } from "react";
import MyDocument from "./document";

function InvoiceRoot({ test }) {
    const [trigger, setTrigger] = useState(false);
    const [instance, update] = usePDF({ document:<MyDocument/> })

    const handelClick =  ()  => {
        update({document:<MyDocument test={true}/>})
        setTrigger(true);


      window.open(instance.url, '_blank')
        setTimeout(() => (setTrigger(false) ), 1000);


      };






  return (
    <div
      style={{
        display: "grid",
        justifyContent: "center",
        backgroundColor: "white",
      }}
    >

      { (trigger)?
        <MyDocument  test={trigger}/>:
        <MyDocument  test={trigger}/>}



        <button onClick={()=>handelClick()}>Download </button>

    </div>
  );
}

export default InvoiceRoot;
