import { usePDF } from '@react-pdf/renderer';
import { useEffect, useState } from 'react';
import MyDocument from "./document";
import InvoiceRoot from './invoiceRoot';


function App() {
  const [instance, updateInstance] = usePDF({ document: <MyDocument/> });
  const [saveClicked, setsaveClicked] = useState(false)


  if (instance.loading) return <div>Loading ...</div>;



  return (

    <div>
    <h1 style={{textAlign:'center'}}>نظام الفاتوره الالكترونيه</h1>

    {(saveClicked)?<InvoiceRoot btnClicked={saveClicked}/>:<InvoiceRoot btnClicked={saveClicked}/>}

    <button  onClick={()=>(()=>setsaveClicked(true),

      console.log(saveClicked))}>
    <a href={instance.url} download="test.pdf" > Dowload</a>
    </button>
    </div>)





}

export default App;
