import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "../invoice.css";
import { faFileInvoice } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useNavigate } from "react-router";
import { useDispatch, useSelector } from "react-redux";

function Search(props) {
  const [data, setData] = useState([]);
  // const [id, setId] = useState(1);
  // const [subTotal, setSubTotal] = useState(0);
  // const [tax, setTax] = useState(0);
  // const [totalAmount, setTotalAmount] = useState(0);
  // const [clientName, setClientName] = useState("");
  // const [clientPhone, setClientPhone] = useState("");
  // const [validation, setValdation] = useState(false);
  // // set by the server side
  // const [invoiceId, setInvoiceId] = useState(1);
  // const [date, setDate] = useState("");
 const navigate = useNavigate()


 const mystate = useSelector((state) => {
  return {
    List: state.List.List,
  };
});

 const dispatch = useDispatch()




  useEffect(async () => {
    try {
      const res = await axios.get("http://localhost:3003/getAll");
      setData(res.data);
      console.log("this correct :", res.data);
    } catch (err) {
      console.log(err);
    }
  }, []);
  console.log("this data :", data);

  return (
    <div className="container mt-5  ">
      <div className="col  ">
      <div className="row justify-content-center  ">
        {data.map((e) => {

          return(

            <div className="card m-3  " style={{ width: "15rem"  }}>
              <div className="card-body ">
               <FontAwesomeIcon size="4x" icon={faFileInvoice}/>
               <hr/>
                <h5 className="card-title">ID:# {e.id}</h5>
                <p className="card-text">Name:{e.clintName}</p>
                <p className="card-text">{e.date}</p>
                <button onClick={()=>(navigate(`/search/${e.id}`))} className="btn btn-primary">
                  Go somewhere
                </button>
              </div>
            </div>


          )
        })}
      </div>
      </div>

      <div className="col-md-10 ">
        <div className="row ">
          <div className="col-xl-3 col-lg-6">
            <div className="card l-bg-cherry">
              <div className="card-statistic-3 p-4">
                <div className="card-icon card-icon-large">
                  <i className="fas fa-shopping-cart"></i>
                </div>
                <div className="mb-4">
                  <h5 className="card-title mb-0">New Orders</h5>
                </div>
                <div className="row align-items-center mb-2 d-flex">
                  <div className="col-8">
                    <h2 className="d-flex align-items-center mb-0">3,243</h2>
                  </div>
                  <div className="col-4 text-right">
                    <span>
                      12.5% <i className="fa fa-arrow-up"></i>
                    </span>
                  </div>
                </div>
                <div
                  className="progress mt-1 "
                  data-height="8"
                  style={{ height: "8px;" }}
                >
                  <div
                    className="progress-bar l-bg-cyan"
                    role="progressbar"
                    data-width="25%"
                    aria-valuenow="25"
                    aria-valuemin="0"
                    aria-valuemax="100"
                    style={{ width: "25%;" }}
                  ></div>
                </div>
              </div>
            </div>
          </div>
          <div className="col-xl-3 col-lg-6">
            <div className="card l-bg-blue-dark">
              <div className="card-statistic-3 p-4">
                <div className="card-icon card-icon-large">
                  <i className="fas fa-users"></i>
                </div>
                <div className="mb-4">
                  <h5 className="card-title mb-0">Customers</h5>
                </div>
                <div className="row align-items-center mb-2 d-flex">
                  <div className="col-8">
                    <h2 className="d-flex align-items-center mb-0">15.07k</h2>
                  </div>
                  <div className="col-4 text-right">
                    <span>
                      9.23% <i className="fa fa-arrow-up"></i>
                    </span>
                  </div>
                </div>
                <div
                  className="progress mt-1 "
                  data-height="8"
                  style={{ height: "8px" }}
                >
                  <div
                    className="progress-bar l-bg-green"
                    role="progressbar"
                    data-width="25%"
                    aria-valuenow="25"
                    aria-valuemin="0"
                    aria-valuemax="100"
                    style={{ width: "25%;" }}
                  ></div>
                </div>
              </div>
