import { PDFDownloadLink } from "@react-pdf/renderer";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changeState, edit, remove } from "../reducers/btnReducer";
import "../invoice.css";
import MyDocument from "./document";
import { Container, Row ,Col} from "react-bootstrap";

function InvoiceRoot({ test }) {
  const [id, setId] = useState(1);

  const mystate = useSelector((state) => {
    return {
      btnSave: state.btnSave.btnSave,
    };
  });



  const dispach = useDispatch();

  useEffect(() => {
    // console.log("id:",id)
    // dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));
  }, []);

  const handelAdd = () => {
    setId(id + 1);
    dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));
  };

  return (



      <Container fluid="true">

      <Row   md style={{backgroundColor:'grey'}}>
       <Col md={4}>me</Col>
       <Col  md={4}>me</Col>
       <Col  md={4}>me</Col>
       <Col md={4} >me</Col>




      </Row>
      <br/>






      {mystate.btnSave.map((item, i) => {

        return (

            <Row key={i}>

              <Col xs={6} md={4}>
                <input
                  style={{

                    textIndent: 5,
                  }}
                  value={item.name}
                  type="text"
                  onChange={(e) => dispach(edit([i, e.target.value, 1]))}
                />
              </Col>


          <Col xs={6} md={4}>
                <input
                  style={{
                    width:70,
                    border: "none",
                    textIndent: 5,
                  }}
                  value={item.quntity}
                  type="number"
                  onChange={(e) => dispach(edit([i, e.target.value, 2]))}
                />

              </Col>

              <Col xs={6} md={4}>
                <input
                  style={{
                    width:70,
                    border: "none",
                    textIndent: 5,
                  }}
                  value={item.price}
                  type="number"
                  onChange={(e) => dispach(edit([i, e.target.value, 3]))}
                />
              </Col>

              <Col xs={2} md={2}>
                <h6 style={{ display: "inline" }}>
                  {item.total}
                </h6>
                </Col>
              <hr />
            </Row>

        );

      })}

         <Row>
         <Col xs={2} md={2}>

         <button onClick={()=>handelAdd()}>Add Line</button>


         </Col>


         </Row>
        </Container>




  );
}

export default InvoiceRoot;
