      <div>
      <h1 style={{textAlign:'center'}}>نظام الفاتوره الالكترونيه</h1>

      <InvoiceRoot btnClicked={saveClicked}/>

      <button  onClick={()=>(()=>setsaveClicked(true), console.log(saveClicked))}>
      <a href={instance.url} download="test.pdf" > Dowload</a>
      </button>
      </div>
    );}

