      <InvoiceRoot btnClicked={saveClicked}/>

      <button  onClick={()=>(()=>setsaveClicked(false), console.log(saveClicked))}>
      <a href={instance.url} download="test.pdf" > Dowload</a>
      </button>
      </div>
    );}



},[saveClicked])

  if (instance.loading) return <div>Loading ...</div>;
