import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "../invoice.css";
import { faFileInvoice } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useNavigate } from "react-router";

function Search(props) {
  const [data, setData] = useState([]);
  // const [id, setId] = useState(1);
  // const [subTotal, setSubTotal] = useState(0);
  // const [tax, setTax] = useState(0);
  // const [totalAmount, setTotalAmount] = useState(0);
  // const [clientName, setClientName] = useState("");
  // const [clientPhone, setClientPhone] = useState("");
  // const [validation, setValdation] = useState(false);
  // // set by the server side
  // const [invoiceId, setInvoiceId] = useState(1);
  // const [date, setDate] = useState("");
 const navigate = useNavigate()






  useEffect(async () => {
    try {
      const res = await axios.get("http://localhost:3003/getAll");
      setData(res.data);
      console.log("this correct :", res.data);
    } catch (err) {
      console.log(err);
    }
  }, []);
  console.log("this data :", data);

  return (
    <div className="container mt-5  ">
      <div className="col  ">
      <div className="row justify-content-center  ">
        {data.map((e) => {

          return(

            <div className="card m-3  " style={{ width: "15rem"  }}>
              <div className="card-body ">
               <FontAwesomeIcon size="4x" icon={faFileInvoice}/>
               <hr/>
                <h5 className="card-title">ID:# {e.id}</h5>
                <p className="card-text">Name:{e.clintName}</p>
                <p className="card-text">{e.date}</p>
                <button onClick={()=>(navigate(`/search/${e.id}`,{ state:{mydata:e}}))} className="btn btn-primary">
                  Go somewhere
                </button>
              </div>
            </div>


          )
        })}
      </div>
      </div>


    </div>
  );
}

export default Search;
