import { usePDF } from '@react-pdf/renderer';
import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import MyDocument from "./document";
import InvoiceRoot from './invoiceRoot';
import btnSave, { changeState } from './reducers/btnReducer';


function App() {

  const [saveClicked, setsaveClicked] = useState(false)
  const [instance, updateInstance] = usePDF({ document: <MyDocument /> });

  const mystate = useSelector((state) => {
    return {
      btnSave: state.btnSave.btnSave,
    };
  });

  const dispach = useDispatch()

  const handelClick = ()=>{   
    dispach(changeState(true))
  }



  return (

    <div>
    <h1 style={{textAlign:'center'}}>نظام الفاتوره الالكترونيه</h1>
    <InvoiceRoot  />



    <button  onClick={()=>(()=>handelClick)}>
    <a href={instance.url} download="test.pdf" > Dowload</a>
    </button>
    </div>)





}

export default App;
