import React, { useEffect, useState } from "react";

import { Document, Page, Text, usePDF, View } from "@react-pdf/renderer";

import { Provider, useDispatch, useSelector } from "react-redux";

import btnSave from "./reducers/btnReducer";
import myStore from "./reducers/store";

function MyDocument({ test }) {


  //input field for products

  const [productName, setproductName] = useState("");
  //   const [productPrice, setproductPrice] = useState("");
  //   const [productQuntity, setproductQuntity] = useState("");
    const [productTotal, setproductTotal] = useState("");

  const [trigger, setTrigger] = useState(false);
  const [instance, updateInstance] = usePDF({ document: productTotal });
  const handelClick = () => {

    setTrigger(true);
    setproductTotal(<MyDocument/>)

    setTimeout(() =>(setproductTotal(""),setTrigger(false)), 5000);

  };

  return (
    <Provider store={myStore}>
      <Document>
        <Page
          size="A4"
          style={{
            backgroundColor: "white",
            marginTop: 50,
            paddingRight: 50,
            paddingLeft: 50,

            height: 500,
          }}
        >
          <View style={{ display: "flex", flexDirection: "row", gap: 100 }}>
            <Text>Invoice</Text>
          </View>
          <View
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "flex-end",
            }}
          >
            <Text>Company Name</Text>
            <Text>Phone Number</Text>
            <Text>Adress</Text>
          </View>

          <View
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "space-around",
            }}
          >
            <View>
              <Text>Client Information:</Text>
            </View>

            <View>
              <Text>#InoviceNo</Text>
            </View>

            <View>
              <Text>Invoice Date</Text>
            </View>
          </View>
          <View
            style={{
              display: "flex",
              flexDirection: "row-reverse",
              justifyContent: "space-around",
              backgroundColor: "grey",
              marginTop: 20,
              marginBottom: 10,
            }}
          >
            {trigger ? (
              <Text>{productName}</Text>
            ) : (
              <input
                value={productName}
                type="text"
                onChange={(e) => setproductName(e.target.value)}
              />
            )}
            <Text>Quntity </Text>
            <Text>Price</Text>
            <Text>Total</Text>
          </View>

          <View
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "space-around",
              marginRight: 1000,
            }}
          >
            <Text>200</Text>
          </View>
          <hr />

          <View
            style={{
              display: "flex",
              flexDirection: "column",
              marginRight: 100,
              paddingTop: 50,
            }}
          >
            <View>
              <Text style={{ display: "inline" }}>Sub Total</Text>
              <Text style={{ display: "inline" }}>3500</Text>
            </View>

            <View>
              <Text style={{ display: "inline" }}>VAT(15%)</Text>
              <Text style={{ display: "inline" }}>500</Text>
            </View>

            <View>
              <Text style={{ display: "inline" }}>Total</Text>
              <Text style={{ display: "inline" }}>4000</Text>
            </View>
            <View>
              {trigger ? (
                <Text>Testtssttsts</Text>
              ) : (
                <button onClick={()=>handelClick()}>
                <a href={instance.url} download="test.pdf" > Dowload</a>
                </button>
              )}
            </View>
          </View>
        </Page>
      </Document>
    </Provider>
  );
}

export default MyDocument;
