import { PDFDownloadLink } from "@react-pdf/renderer";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changeState, edit, remove } from "../reducers/btnReducer";
import "../invoice.css";
import MyDocument from "./document";
import { Container, Row ,Col} from "react-bootstrap";

function InvoiceRoot({ test }) {
  const [id, setId] = useState(1);

  const mystate = useSelector((state) => {
    return {
      btnSave: state.btnSave.btnSave,
    };
  });

  const dispach = useDispatch();

  useEffect(() => {
    // console.log("id:",id)
    // dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));
  }, []);

  const handelAdd = () => {
    setId(id + 1);
    dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));
  };

  return (
    <div
      style={{
        backgroundColor: "white",
        margin: 50,
        paddingTop: 0,
        paddingRight: 100,
        paddingLeft: 100,
        width: 1400,
        height: 1000,
      }}
    >
      <div style={{ display: "flex", flexDirection: "row", gap: 100 }}>
        <h3>Invoice</h3>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "flex-end",
        }}
      >
        <h3>Company Name</h3>
        <h3>Phone Number</h3>
        <h3>Adress</h3>
      </div>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
        }}
      >
        <div>
          <h3>Client Information:</h3>
        </div>

        <div>
          <h3>#InoviceNo</h3>
        </div>

        <div>
          <h3>Invoice Date</h3>
        </div>
      </div>
   <Container style={{backgroundColor:'grey'}}>
   <Row>
   <Col xs={3} md={3}>
   <h4>Product Name</h4></Col>
   <Col xs={3} md={3}>
   <h4>Quntity </h4></Col>
   <Col xs={3} md={3}>
   <h4>Price</h4></Col>
   <Col xs={2} md={2}>
        <h4>Total</h4>
        </Col>
        </Row>


   </Container>
     <br/>
      <Container>

      {mystate.btnSave.map((item, i) => {


        return (

            <Row key={i}>

              <Col xs={8} md={3}>
                <input
                  style={{

                    textIndent: 5,
                  }}
                  value={item.name}
                  type="text"
                  onChange={(e) => dispach(edit([i, e.target.value, 1]))}
                />
              </Col>


          <Col xs={3} md={3}>
                <input
                  style={{
                    width:70,
                    border: "none",
                    textIndent: 5,
                  }}
                  value={item.quntity}
                  type="number"
                  onChange={(e) => dispach(edit([i, e.target.value, 2]))}
                />

              </Col>

              <Col xs={6} md={3}>
                <input
                  style={{
                    width:70,
                    border: "none",
                    textIndent: 5,
                  }}
                  value={item.price}
                  type="number"
                  onChange={(e) => dispach(edit([i, e.target.value, 3]))}
                />
              </Col>

              <Col xs={2} md={2}>
                <h3 style={{ display: "inline" }}>
                  {item.total}
                </h3>
                </Col>
              <hr />
            </Row>

        );

      })}
        </Container>






      <button onClick={() => handelAdd()}>Add Line</button>

      <div
        style={{
          display: "flex",
          flexDirection: "column",

          paddingTop: 50,
        }}
      >
        <div>
          <h3 style={{ display: "inline" }}>Sub Total</h3>
          <h3 style={{ display: "inline" }}>3500</h3>
        </div>

        <div>
          <h3 style={{ display: "inline" }}>VAT(15%)</h3>
          <h3 style={{ display: "inline" }}>500</h3>
        </div>

        <div>
          <h3 style={{ display: "inline" }}>Total</h3>
          <h3 style={{ display: "inline" }}>4000</h3>
        </div>
      </div>

      <PDFDownloadLink
        document={<MyDocument test={mystate.btnSave} />}
        fileName="somename.pdf"
      >
        {({ blob, url, loading, error }) => (
          <div>
            {loading ? (
              "Loading document..."
            ) : (
              <button> "Download now!"</button>
            )}
          </div>
        )}
      </PDFDownloadLink>
    </div>
  );
}

export default InvoiceRoot;
