import React from "react";
import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Button, Form, Label, Control } from "react-bootstrap";
import axios from "axios";

function Login() {

  const handle = (e) => {
    localStorage.setItem("token", e);
    console.log(localStorage.getItem("token"));
  };

  async function getInfo(e) {
    e.preventDefault();

    console.log();
    let obj = {
      username: e.target.form[0].value,
      password: e.target.form[1].value,
    };
    try {
      const res = await axios.post("/login", obj);
      console.log(res.data);
      handle(res.data);
    } catch (err) {
      console.log(err);
    }
  }

  return (
    <div>
      <form>
        <Form.Group className="mb-3 w-25 controlId" controlId="formBasicEmail">
          <Form.Label>User Name</Form.Label>
          <Form.Control type="text" placeholder="User Name" />
        </Form.Group>

        <Form.Group className="mb-3 w-25 " controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicCheckbox"></Form.Group>
        <Button
          variant="primary"
          type="submit"
          onClick={(e) => {
            getInfo(e);
          }}
        >
          Submit
        </Button>
      </form>
    </div>
  );
}

export default Login;
