import { Font, PDFDownloadLink } from "@react-pdf/renderer";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changeState, edit, remove } from "../reducers/btnReducer";
import "../invoice.css";
import MyDocument from "./document";
import { faPlus, faTrash } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import axios from "axios";

function InvoiceRoot({ test }) {
  const [id, setId] = useState(1);
  const [subTotal, setSubTotal] = useState(0);
  const [tax, setTax] = useState(0);
  const [totalAmount, setTotalAmount] = useState(0);
  const [clientName, setClientName] = useState("");
  const [clientPhone, setClientPhone] = useState("");
  // set by the server side
  const [invoiceId, setInvoiceId] = useState(1);
  const [date, setDate] = useState("");


  const mystate = useSelector((state) => {
    return {
      btnSave: state.btnSave.btnSave,
    };
  });

  const dispach = useDispatch();

  const handelAdd = () => {
    setId(id + 1);
    dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));
  };
  const handelPost = async () => {
    let obj = {
      date:date,
      clintName: clientName,
      clintPhone: clientPhone,
      subTotal: subTotal,
      tax: tax,
      totalAmount: totalAmount,
      itemId: mystate.btnSave,
    };
    try {
      const res = await axios.post("http://localhost:3003/post", obj);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(async () => {
    try {
      const res = await axios.get("http://localhost:3003/getApi");
      setInvoiceId(res.data[0]);
      setDate(res.data[1]);
      console.log("this correct id:", res.data);
    } catch (err) {
      console.log(err);
      console.log("this is id:", invoiceId);
    }
  }, []);

  useEffect(() => {
    setTax((subTotal * 15) / 100);
    let total = subTotal + (subTotal * 15) / 100;
    setTotalAmount(total);
  }, [subTotal]);

  const handelCalculation = () => {
    let data = 0;

    for (let i = 0; i < mystate.btnSave.length; i++) {
      data += mystate.btnSave[i].total;
    }

    setSubTotal(data);

    console.log("Sub: ", subTotal, "Tax:", tax, "total", totalAmount);
  };

  return (
    <div
      classNameName="page-content container  myshadow "
      style={{ backgroundColor: "white", borderRadius: 10, marginTop: "1%"  }}
    >
      <h2 style={{ textAlign: "center" }}>Create New Invoice</h2>
      <div classNameName="page-header text-blue-d2">
        <h1 classNameName="page-title text-secondary-d1">
          Invoice
          <small classNameName="page-info">ID:#{invoiceId}</small>
        </h1>

        <div classNameName="page-tools">
          <div classNameName="action-buttons">
            <PDFDownloadLink
              document={<MyDocument test={mystate.btnSave} />}
              fileName="somename.pdf"
            >
              {({ blob, url, loading, error }) =>

                  <button
                    onClick={() => (handelPost(),window.open(url, "_blank")) }
                    classNameName="btn btn-primary"
                    data-title="Print"
                  >
                    Download
                  </button>

              }
            </PDFDownloadLink>
          </div>
        </div>
      </div>

      <div classNameName="container px-0">
        <div classNameName="row mt-1">
          <div classNameName="col-12 col-lg-10 offset-lg-1">
            <div classNameName="row">
              <div classNameName="col-12">

              </div>
            </div>

            <hr classNameName="row brc-default-l1 mx-n1 mb-4" />

            <div classNameName="row">
              <div classNameName="col-sm-6">
                <div>
                  <span classNameName="text-sm text-grey-m2 align-middle">
                    Client Name:
                  </span>
                  <br />
                  <span classNameName="text-600 text-110 text-blue align-middle">
                    <input
                      value={clientName}
                      onChange={(e) => setClientName(e.target.value)}
                    ></input>
                  </span>
                </div>
                <div>
                  <span classNameName="text-sm text-grey-m2 align-middle">
                    PhoneNo:
                  </span>
                  <br />
                  <span classNameName="text-600 text-110 text-blue align-middle">
                    <input
                      value={clientPhone}
                      onChange={(e) => setClientPhone(e.target.value)}
                    ></input>
                  </span>
                </div>
              </div>

              <div classNameName="text-95 col-sm-6 align-self-start d-sm-flex justify-content-end">
                <hr classNameName="d-sm-none" />
                <div classNameName="text-grey-m2">
                  <div classNameName="mt-1 mb-2 text-secondary-m1 text-600 text-125">
                    Invoice
                  </div>

                  <div classNameName="my-2">
                    <span classNameName="text-600 text-90">ID:</span> #{invoiceId}
                  </div>

                  <div classNameName="my-2">
                    <span classNameName="text-600 text-90">Issue Date:</span> {date}
                  </div>
                </div>
              </div>
            </div>

            <div classNameName="mt-4">
              <div classNameName="row text-600 text-white bgc-default-tp1 py-25">
                <div classNameName=" col-1">#</div>
                <div classNameName="col-4">Description</div>
                <div classNameName=" col">Qty</div>
                <div classNameName=" col">Unit Price</div>
                <div classNameName="col">Amount</div>
              </div>

              <div classNameName=" text-95 text-secondary-d3"   style={{ height:100, overflow:'scroll' }}>

                {mystate.btnSave.map((e, i) => {
                  return (
                    <div

                      classNameName={
                        i % 2 == 0
                          ? "row mb-2 mb-sm-0 py-25 "
                          : "row mb-2 mb-sm-0 py-25 bgc-default-l4"
                      }
                      key={i}
                    >
                      <div classNameName="col-1">{e.id}</div>
                      <div classNameName="col-4">
                        <input
                          placeholder="Product Name"
                          value={e.name}
                          onChange={(e) =>
                            dispach(edit([i, e.target.value, 1]))
                          }
                        ></input>
                      </div>
                      <div classNameName="col">
                        <input
                          maxLength={4}
                          value={e.quntity}
                          onChange={(e) => (
                            dispach(edit([i, e.target.value, 2])),
                            handelCalculation()
                          )}
                          style={{ width: 60 }}
                        ></input>
                      </div>
                      <div classNameName="col">
                        <input
                          maxLength={6}
                          value={e.price}
                          onChange={(e) => (
                            dispach(edit([i, e.target.value, 3])),
                            handelCalculation()
                          )}
                          style={{ width: 65 }}
                        ></input>
                      </div>
                      <div classNameName="col ">
                        {e.total}
                        <FontAwesomeIcon
                          onClick={() => (dispach(remove(e.id)), setId(id-1))}
                          style={{ marginLeft: "50%", cursor: "pointer" }}
                          size="lg"
                          color="#fc4765"
                          icon={faTrash}
                        />{" "}
                      </div>
                    </div>
                  );
                })}
                </div>







              <div classNameName="row border-b-2 brc-default-l2"></div>
              <hr />
              <h5 onClick={() => handelAdd()} style={{ cursor: "pointer" }}>
                {" "}
                <FontAwesomeIcon size="1x" color="green" icon={faPlus} />
                Add New Line
              </h5>

              <div classNameName="row ">
                <div classNameName="col-12 col-sm-7 text-grey-d2 text-95 mt-2 mt-lg-0"></div>

                <div classNameName="col-12 col-sm-5 text-grey text-90 order-first order-sm-last">
                  <div classNameName="row my-2">
                    <div classNameName="col-7 text-right">SubTotal</div>
                    <div classNameName="col-5">
                      <span classNameName="text-120 text-secondary-d1">
                        ${subTotal}
                      </span>
                    </div>
                  </div>

                  <div classNameName="row my-2">
                    <div classNameName="col-7 text-right">Tax (10%)</div>
                    <div classNameName="col-5">
                      <span classNameName="text-110 text-secondary-d1">${tax}</span>
                    </div>
                  </div>

                  <div classNameName="row my-2 align-items-center bgc-primary-l3 p-2">
                    <div classNameName="col-7 text-right">Total Amount</div>
                    <div classNameName="col-5">
                      <span classNameName="text-150 text-success-d3 opacity-2">
                        ${totalAmount}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              <hr />
            </div>
          </div>
        </div>
      </div>
      <footer
      className="text-center text-lg-start text-dark"
      >

<section
         className="d-flex justify-content-between p-4 text-white"
         >

  <div className="me-5">
    <span>Get connected with us on social networks:</span>
  </div>

  <div>
    <a href="" className="text-white me-4">
      <i className="fab fa-facebook-f"></i>
    </a>
    <a href="" className="text-white me-4">
      <i className="fab fa-twitter"></i>
    </a>
    <a href="" className="text-white me-4">
      <i className="fab fa-google"></i>
    </a>
    <a href="" className="text-white me-4">
      <i className="fab fa-instagram"></i>
    </a>
    <a href="" className="text-white me-4">
      <i className="fab fa-linkedin"></i>
    </a>
    <a href="" className="text-white me-4">
      <i className="fab fa-github"></i>
    </a>
  </div>

</section>

<section className="">
  <div className="container text-center text-md-start mt-5">

    <div className="row mt-3">

      <div className="col-md-3 col-lg-4 col-xl-3 mx-auto mb-4">

        <h6 className="text-uppercase fw-bold">Company name</h6>
        <hr
            className="mb-4 mt-0 d-inline-block mx-auto"
            />
        <p>
          Here you can use rows and columns to organize your footer
          content. Lorem ipsum dolor sit amet, consectetur adipisicing
          elit.
        </p>
      </div>

      <div className="col-md-2 col-lg-2 col-xl-2 mx-auto mb-4">

        <h6 className="text-uppercase fw-bold">Products</h6>
        <hr
            className="mb-4 mt-0 d-inline-block mx-auto"
            />

      </div>

      <div className="col-md-3 col-lg-2 col-xl-2 mx-auto mb-4">

        <h6 className="text-uppercase fw-bold">Useful links</h6>
        <hr
            className="mb-4 mt-0 d-inline-block mx-auto"
            />

      </div>



      <div className="col-md-4 col-lg-3 col-xl-3 mx-auto mb-md-0 mb-4">

        <h6 className="text-uppercase fw-bold">Contact</h6>
        <hr
            className="mb-4 mt-0 d-inline-block mx-auto"
            />

      </div>

    </div>

  </div>
</section>



<div
     className="text-center p-3"
     >
   2020 Copyright:

</div>

</footer>
    </div>
  );
}

export default InvoiceRoot;
