import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function Login() {

  const handle = (e) => {
    localStorage.setItem("token", e);
    console.log(localStorage.getItem("token"));
  };

  async function getInfo(e) {
    e.preventDefault();

    console.log();
    let obj = {
      username: e.target.form[0].value,
      password: e.target.form[1].value,
    };
    console.log(obj)
    try {
      const res = await axios.post("http://localhost:3003/login", obj);
      console.log(res.data);
      handle(res.data);
    } catch (err) {
      console.log(err);
    }
  }

  return (


    <section className="vh-80" style={{marginTop:"10%"}}>

    <div className="container-fluid h-custom" >
      <div className="row d-flex justify-content-center align-items-center h-100">
        <div className="col-md-9 col-lg-6 col-xl-5">
          <img src="https://www.accountsportal.com/img/blog/1148/an-introduction-to-invoices-banner.png" className="img-fluid"
            alt="Sample image"
            style={{borderRadius:100}}
            
            />
        </div>
        <div className="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
          <form>
           
  
            <div className="divider d-flex align-items-center my-4">
             
            </div>
  
         
            <div className="form-outline mb-4">
            <label className="form-label" for="form3Example3">Email address</label>

              <input type="email" id="form3Example3" className="form-control form-control-lg"
                placeholder="Enter a valid email address" />
            </div>
  
            <div className="form-outline mb-3">
              <input type="password" id="form3Example4" className="form-control form-control-lg"
                placeholder="Enter password" />
              <label className="form-label" for="form3Example4">Password</label>
            </div>
  
            <div className="d-flex justify-content-between align-items-center">
        
              <div className="form-check mb-0">
                <input className="form-check-input me-2" type="checkbox" value="" id="form2Example3" />
                <label className="form-check-label" for="form2Example3">
                  Remember me
                </label>
              </div>
          
            </div>
  
            <div className="text-center text-lg-start mt-4 pt-2">
              <button type="button" className="btn btn-primary btn-lg"
                style={{paddingLeft: "2.5rem", paddingRight: "2.5rem"}}>Login</button>
            
            </div>
  
          </form>
        </div>
      </div>
    </div>
   
  </section>





   
  );
}

export default Login;
