import { usePDF } from "@react-pdf/renderer";
import React, { useEffect, useState } from "react";
import MyDocument from "./document";

function InvoiceRoot({ test }) {
    const [trigger, setTrigger] = useState(false);
    const [instance, update] = usePDF({ document:<MyDocument/> })

    const handelClick = () => {
        setTrigger(true);

        update({document:<MyDocument/>})

        setTimeout(() => (setTrigger(false)), 500);
      };




  return (
    <div
      style={{
        display: "grid",
        justifyContent: "center",
        backgroundColor: "white",
      }}
    >

      { (trigger)?
        <MyDocument  test={trigger}/>:
        <MyDocument  test={trigger}/>}



        <button onClick={()=>handelClick()}>{(trigger)}<a href={instance.url} download="text.pdf">download</a> </button>

    </div>
  );
}

export default InvoiceRoot;
