import { PDFDownloadLink } from "@react-pdf/renderer";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {  changeState, edit, remove } from "../reducers/btnReducer";
import "../invoice.css";
import MyDocument from "./document";

function InvoiceRoot({ test }) {
  const [id, setId] = useState(1);

  const mystate = useSelector((state) => {
    return {
      btnSave: state.btnSave.btnSave,
    };
  });

  const dispach = useDispatch();

  useEffect(() => {

    // console.log("id:",id)
    // dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));

  }, []);

  const handelAdd = () => {
    setId(id + 1);
    dispach(changeState({ id: id, name: "", quntity: 0, price: 0, total: 0 }));

  };


  return (
    <div
      style={{
        backgroundColor: "white",
        margin: 50,
        paddingTop: 0,
        paddingRight: 100,
        paddingLeft: 100,
        width: 800,
        height: 1000,
      }}
    >
      <div style={{ display: "flex", flexDirection: "row", gap: 100 }}>
        <h3>Invoice</h3>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "flex-end",
        }}
      >
        <h3>Company Name</h3>
        <h3>Phone Number</h3>
        <h3>Adress</h3>
      </div>

      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
        }}
      >
        <div>
          <h3>Client Information:</h3>
        </div>

        <div>
          <h3>#InoviceNo</h3>
        </div>

        <div>
          <h3>Invoice Date</h3>
        </div>
      </div>
      <div
        style={{
          display: "flex",
          flexDirection: "row-reverse",
          justifyContent: "space-around",
          backgroundColor: "grey",
          marginTop: 20,
          marginBottom: 10,
        }}
      >
        <h3>Total</h3>
        <h3>Price</h3>
        <h3>Quntity </h3>
        <h3>Product Name</h3>
      </div>

      {mystate.btnSave.map((item, i) => {
        return (
          <div key={i}>
            <div
              className="divFlexx"
              style={{
                display: "flex",
                flexDirection: "row",
                margin: 10,
              }}
            >
              <button onClick={() => dispach(remove(item))}>Delete</button>

              <textarea
                style={{
                  width: 200,
                  marginLeft: 20,
                  marginTop: 10,
                  textIndent: 5,
                }}
                value={item.name}
                rows={2}
                type="text"
                onChange={(e) => dispach(edit([i, e.target.value, 1]))}
              />

              <input
                style={{
                  width: 80,
                  marginLeft: 90,
                  border: "none",
                  textIndent: 5,
                }}
                value={item.quntity}
                type="number"
                onChange={(e) => dispach(edit([i, e.target.value, 2]))}
              />

              <input
                style={{
                  width: 80,
                  marginLeft: 120,
                  border: "none",
                  textIndent: 5,
                }}
                value={item.price}
                type="number"
                onChange={(e) => dispach(edit([i, e.target.value, 3]))}
              />

              <h3 style={{ display: "inline", marginLeft: 100 }}>
                {item.total}
              </h3>
            </div>
            <hr />
          </div>
        );
      })}

      <button onClick={() => handelAdd()}>Add Line</button>

      <div
        style={{
          display: "flex",
          flexDirection: "column",

          paddingTop: 50,
        }}
      >
        <div>
          <h3 style={{ display: "inline" }}>Sub Total</h3>
          <h3 style={{ display: "inline" }}>3500</h3>
        </div>

        <div>
          <h3 style={{ display: "inline" }}>VAT(15%)</h3>
          <h3 style={{ display: "inline" }}>500</h3>
        </div>

        <div>
          <h3 style={{ display: "inline" }}>Total</h3>
          <h3 style={{ display: "inline" }}>4000</h3>
        </div>
      </div>



    </div>
  );
}

export default InvoiceRoot;
