import { PDFDownloadLink } from "@react-pdf/renderer";
import React, {useState } from "react";
import MyDocument from "./document";

function InvoiceRoot({ test }) {

    const [name,setName] = useState("")
    const [quntity,setQuntity] =  useState("")
    const [price,setPrice] =  useState("")
    const [total,setTotal] =  useState("")



  return (

    <div

    style={{
      backgroundColor: "white",
      marginTop: 50,

      height: 800,
    }}
  >
    <div style={{ display: "flex", flexDirection: "row", gap: 100 }}>
      <h3>Invoice</h3>
    </div>
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "flex-end",
      }}
    >
      <h3>Company Name</h3>
      <h3>Phone Number</h3>
      <h3>Adress</h3>
    </div>

    <div
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-around",
      }}
    >
      <div>
        <h3>Client Information:</h3>
      </div>

      <div>
        <h3>#InoviceNo</h3>
      </div>

      <div>
        <h3>Invoice Date</h3>
      </div>
    </div>
    <div
      style={{
        display: "flex",
        flexDirection: "row-reverse",
        justifyContent: "space-around",
        backgroundColor: "grey",
        marginTop: 20,
        marginBottom: 10,
      }}
    >
    <h3>Total</h3>
    <h3>Price</h3>
    <h3>Quntity </h3>
    <h3>Product Name</h3>
    </div>

    <div
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: 'space-around',
        marginLeft: 20,
      }}
    >

    <input
    style={{width:200}}
    value={name}
    type="text"
    onChange={(e) => setName(e.target.value)}/>
    <input
    style={{width:80}}
    value={quntity}
    type="number"
    onChange={(e) => setQuntity(e.target.value)}/>
    <input
    value={price}
    type="text"
    onChange={(e) => setPrice(e.target.value)}/>

    <h3>200</h3>


            </div>
    <hr />

    <div
      style={{
        display: "flex",
        flexDirection: "column",

        paddingTop: 50,
      }}
    >
      <div>
        <h3 style={{ display: "inline" }}>Sub Total</h3>
        <h3 style={{ display: "inline" }}>3500</h3>
      </div>

      <div>
        <h3 style={{ display: "inline" }}>VAT(15%)</h3>
        <h3 style={{ display: "inline" }}>500</h3>
      </div>

      <div>
        <h3 style={{ display: "inline" }}>Total</h3>
        <h3 style={{ display: "inline" }}>4000</h3>
      </div>
    </div>





     <PDFDownloadLink document={<MyDocument  />} fileName="somename.pdf">
     {({ blob, url, loading, error }) =>
       loading ? 'Loading document...' : 'Download now!'
     }
   </PDFDownloadLink>



    </div>
  );
}

export default InvoiceRoot;
