import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "."

function Login() {

  const handle = (e) => {
    localStorage.setItem("token", e);
    console.log(localStorage.getItem("token"));
  };

  async function getInfo(e) {
    e.preventDefault();

    console.log();
    let obj = {
      username: e.target.form[0].value,
      password: e.target.form[1].value,
    };
    try {
      const res = await axios.post("/login", obj);
      console.log(res.data);
      handle(res.data);
    } catch (err) {
      console.log(err);
    }
  }

  return (


    <div claasName="container">
    <div claasName="row">
        <div claasName="col-lg-3 col-md-2"></div>
        <div claasName="col-lg-6 col-md-8 login-box">
            <div claasName="col-lg-12 login-key">
                <i claasName="fa fa-key" aria-hidden="true"></i>
            </div>
            <div claasName="col-lg-12 login-title">
                ADMIN PANEL
            </div>

            <div claasName="col-lg-12 login-form">
                <div claasName="col-lg-12 login-form">
                    <form>
                        <div claasName="form-group">
                            <label claasName="form-control-label">USERNAME</label>
                            <input type="text" claasName="form-control"/>
                        </div>
                        <div claasName="form-group">
                            <label claasName="form-control-label">PASSWORD</label>
                            <input type="password" claasName="form-control" i/>
                        </div>

                        <div claasName="col-lg-12 loginbttm">
                            <div claasName="col-lg-6 login-btm login-text">
                             
                            </div>
                            <div claasName="col-lg-6 login-btm login-button">
                                <button type="submit" claasName="btn btn-outline-primary">LOGIN</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
            <div claasName="col-lg-3 col-md-2"></div>
        </div>
    </div>
    </div>






   
  );
}

export default Login;
